trigger_prototype.recovery_expression:
(
	{SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select BootVolume From Win32_Volume Where Name='{#NAME}'].last()}=1
	and
	(
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\Free Megabytes"].last()} >= ( {$SCOMMPWSOS_LDISK_FREEMB_SYS_ERR_TH} + ( {$SCOMMPWSOS_LDISK_FREEMB_SYS_ERR_SMP} * {SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select Capacity From Win32_Volume Where Name='{#NAME}'].last()} ) ) * {$SCOMMPWSOS_LDISK_FREEMB_SYS_ERR_RE}
		or
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\% Free Space",900].last()} >= {$SCOMMPWSOS_LDISK_FREEPR_SYS_ERR_TH} * {$SCOMMPWSOS_LDISK_FREEPR_SYS_ERR_RE}
	)
)
or
(
	{SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select BootVolume From Win32_Volume Where Name='{#NAME}'].last()}=0
	and
	(
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\Free Megabytes"].last()} >= ( {$SCOMMPWSOS_LDISK_FREEMB_NSYS_ERR_TH} + ( {$SCOMMPWSOS_LDISK_FREEMB_NSYS_ERR_SMP} * {SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select Capacity From Win32_Volume Where Name='{#NAME}'].last()} ) ) * {$SCOMMPWSOS_LDISK_FREEMB_NSYS_ERR_RE}
		or
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\% Free Space",900].last()} >= {$SCOMMPWSOS_LDISK_FREEPR_NSYS_ERR_TH} * {$SCOMMPWSOS_LDISK_FREEPR_SYS_ERR_RE}
	)
)

(
	{SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select BootVolume From Win32_Volume Where Name='{#NAME}'].last()}=1
	and
	(
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\Free Megabytes"].last()} >= ( {$SCOMMPWSOS_LDISK_FREEMB_SYS_WAR_TH} + ( {$SCOMMPWSOS_LDISK_FREEMB_SYS_WAR_SMP} * {SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select Capacity From Win32_Volume Where Name='{#NAME}'].last()} ) ) * {$SCOMMPWSOS_LDISK_FREEMB_SYS_WAR_RE}
		or
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\% Free Space",900].last()} >= {$SCOMMPWSOS_LDISK_FREEPR_SYS_WAR_TH} * {$SCOMMPWSOS_LDISK_FREEPR_SYS_WAR_RE}
	)
)
or
(
	{SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select BootVolume From Win32_Volume Where Name='{#NAME}'].last()}=0
	and
	(
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\Free Megabytes"].last()} >= ( {$SCOMMPWSOS_LDISK_FREEMB_NSYS_WAR_TH} + ( {$SCOMMPWSOS_LDISK_FREEMB_NSYS_WAR_SMP} * {SCOM MP Windows Server Operating System:wmi.get[root\cimv2,Select Capacity From Win32_Volume Where Name='{#NAME}'].last()} ) ) * {$SCOMMPWSOS_LDISK_FREEMB_NSYS_WAR_RE}
		or
		{SCOM MP Windows Server Operating System:perf_counter_en["\LogicalDisk({#I})\% Free Space",900].last()} >= {$SCOMMPWSOS_LDISK_FREEPR_NSYS_WAR_TH} * {$SCOMMPWSOS_LDISK_FREEPR_SYS_WAR_RE}
	)
)

step.params:
parser = JSON.parse(value);
for (var i in parser) {
  parser[i]['NamePerfmon'] = parser[i]['Name'].replace(/\\+/g,'\\').replace(/\\$/g,'');
  parser[i]['DeviceIDGUID'] = parser[i]['DeviceID'].match('\{(.*?)\}')[0];
};
return JSON.stringify(parser);

parser = JSON.parse(value);
 for (var i in parser) {
  parser[i]['NamePerfmon'] = parser[i]['InterfaceDescription'].replace('#','_').replace('(','[').replace(')',']');
 };
return JSON.stringify(parser);
